"use strict";var imageSlider={props:["context","alias"],data:function(){return{images:[],currentNumber:0,timer:null,baseurl:"/api/images"}},created:function(){this.getFiles(),this.startRotation()},methods:{startRotation:function(){this.timer=setInterval(this.next,3e3)},stopRotation:function(){clearTimeout(this.timer),this.timer=null},next:function(){this.currentNumber+=1,this.currentNumber===this.images.length&&(this.currentNumber=0)},prev:function(){this.currentNumber-=1,this.currentNumber<0&&(this.currentNumber=this.images.length-1)},getFiles:function(){self=this,axios.get(this.baseurl,{params:{context:this.context,alias:this.alias}}).then(function(t){self.images=t.data}).catch(function(t){})}},template:'\n        <div class="slideshow" v-if="images.length">\n        <p class="navigation">\n            <a @click="prev">◀︎</a><a @click="next">▶︎</a>\n        </p>\n        <transition name="fade">\n        <img :src="images[currentNumber].path" v-on:mouseover="stopRotation" v-on:mouseout="startRotation" :key="images[currentNumber].id"/>\n        </transition>\n        </div>\n        <div v-else>No hay imágenes</div>\n    '},articleImages=new Vue({el:"#mh-images",components:{"image-slider":imageSlider}});